import React, {Component} from 'react';
import { Button } from 'react-bootstrap';
import './filter.css';

class Clothing extends Component {
  constructor(props) {
    super(props);
      this.state = {
      selectedProduct: {},
      isAdded: false
    };
  }
  addToCart(image, name, price, id, quantity) {
    this.setState(
      {
        selectedProduct: {
          image: image,
          name: name,
          price: price,
          id: id
        }
      },
      function() {
        this.props.addToCart(this.state.selectedProduct);
      }
    );
    this.setState(
      {
        isAdded: true
      },
      function() {
        setTimeout(() => {
          this.setState({
            isAdded: false,
            selectedProduct: {}
          });
        }, 3500);
      }
    );
  }
  render() {
    return (
      <div className="clothing-box">
        <p><img src={"images/" + this.props.item.id + ".jpg"} alt={this.props.item.name}></img></p>
        <h4>{this.props.item.ocassion} {this.props.item.name}</h4>
        <h4 id="price">${this.props.item.price}</h4>
        <Button id = "cart" onSelect = {this.addedToCart.bind(this)}>Add to Cart!</Button>
      </div>
    );
  }
}

export default Clothing;
