import React, {Component} from 'react';
import { Button } from 'react-bootstrap';
import './filter.css';

class Clothing extends Component {
  constructor(props) {
    super(props);
    this.state = {
        added: this.props.item.added === "no" ? false : true,
    };
  }
  addedToCart() {
    this.props.addToCart(this.props.item, this.state.added)
    this.setState({added: !this.state.added});
  }
  render() {
    return (
      <div className="clothing-box">
        <p><img src={"images/" + this.props.item.id + ".jpg"} alt={this.props.item.name}></img></p>
        <h4>{this.props.item.ocassion} {this.props.item.name}</h4>
        <h4 id="price">${this.props.item.price}</h4>
        <Button id = "cart" onSelect = {this.props.updateCart(this.id, 1, false)}>Add to Cart!</Button>
      </div>
    );
  }
}

export default Clothing;
