import React, { Component } from 'react';
import { DropdownButton, MenuItem } from 'react-bootstrap';
import DisplayList from './DisplayList';
import './filter.css';

class FilteredList extends Component{
	constructor(props){
		super(props);
		this.state = {
      name: "",
			type: "",
			occasion: "",
      bool: false,
		};

	}

onFilterType = (event) => {
	this.setState({type: event});
}

onFilterOccasion = (event) => {
  this.setState({occasion: event});
}

onSort = (event) => {
	this.setState({sort: event});
}

filterItem = (item) => {
	  if (this.state.type === "All"){
     return 1;
    }

    if(item.type === this.state.type){
    return 1;
    }

    if(this.state.type === ""){
    return 1;
    }
}

filterOccasion = (item) => {
  if(item.occasion === this.state.occasion){
      return 1;
    }
    if (this.state.occasion === "All"){
      return 1;
    }
    if(this.state.occasion === ""){
     return 1;
    }
}

sortPrice = (p1, p2) => {
	if (this.state.sort === "") {
      return 0;
    } else if (this.state.sort === "Low to High"){
      return p1.price - p2.price;
    } else if (this.state.sort === "High to Low"){
      return p2.price - p1.price;
    }
}

toggleState() {
  this.setState({bool: !this.state.bool});
}

  render(){
    return (
        <div className = "filter">

          <DropdownButton id= "typeDropdown" title={"Type"}>
            <MenuItem eventKey= "All" onSelect={this.onFilterType}>All</MenuItem>
            <MenuItem eventKey= "Shirt" onSelect={this.onFilterType}>Shirt</MenuItem>
            <MenuItem eventKey= "Bottoms" onSelect={this.onFilterType}>Bottoms</MenuItem>
            <MenuItem eventKey= "Dress" onSelect={this.onFilterType}>Dress</MenuItem>
          </DropdownButton>

          <DropdownButton id= "typeDropdown" title={"Occasion"}>
            <MenuItem eventKey= "All" onSelect={this.onFilterOccasion}>All</MenuItem>
            <MenuItem eventKey= "Party" onSelect={this.onFilterOccasion}>Party</MenuItem>
            <MenuItem eventKey= "Casual" onSelect={this.onFilterOccasion}>Casual</MenuItem>
            <MenuItem eventKey= "Work" onSelect={this.onFilterOccasion}>Work</MenuItem>
          </DropdownButton>

					<DropdownButton id= "typeDropdown" title={"Sort by Price"}>
            <MenuItem eventKey= "Low to High" onSelect={this.sortPrice}>Low to High</MenuItem>
            <MenuItem eventKey= "High to Low" onSelect={this.sortPrice}>High to Low</MenuItem>
						<MenuItem eventKey= "None" onSelect={this.sortPrice}>None</MenuItem>
          </DropdownButton>
           <DisplayList items = {this.state.bool?
                          this.props.items.filter(this.filterItem).filter(this.filterOccasion).sort(this.sortPrice):
                          this.props.items.filter(this.filterItem).filter(this.filterOccasion)} />
        </div>


    );
  }

}

export default FilteredList;
