import React, {Component} from 'react';
import { Thumbnail, Grid, Row, Col } from 'react-bootstrap';
import './filter.css';
import Clothing from "./Clothing";

class DisplayList extends Component {

  constructor(props) {
    super(props);
  }

  renderList() {
    const items = this.props.items.map(item => {
      return (
        <li> {item.name}, {item.type} </li>
      );
    });
    if (items.length == 0) {
      return (<div style={{ marginTop: "100px"}}><h2>No clothes available under selected filters. Please make your search broader.</h2></div>);
    } else {
      return items;
    }
  }

  render() {
    return (
      this.renderList()
    );
  }
}

export default DisplayList;
