import React, {Component} from 'react';
import './filter.css';
import Clothing from "./Clothing";
import Cart from "./Cart";
import {Row, Col} from 'react-bootstrap';

class DisplayList extends Component {
  render(){
    if (items.length > 0) {
      return (<Row>
        <Col xs={8}><p><Clothing item={this.props.items}/></p></Col>
        <Col xs={4}><Cart allCars = {this.props.allCars} updateCart = {this.props.updateCart} cart = {this.props.cart} total = {this.props.total}/></Col>
        </Row>)
    }
    else {
      return (<Row>
        <Col xs={8}><p><div style={{ marginTop: "100px"}}><h2>There are no clothes available under these filters. Please broaden your search! </h2></div>)</p></Col>
        <Col xs={4}><Cart allCars = {this.props.allCars} updateCart = {this.props.updateCart} cart = {this.props.cart} total = {this.props.total}/></Col>
        </Row>)
    }
  }
}

export default DisplayList;
